package com.etermax.spacehorse.core.shop.action;

import static org.junit.Assert.assertNotNull;
import static org.junit.Assert.assertTrue;

import java.util.List;

import org.junit.Assert;
import org.junit.Test;

import com.etermax.spacehorse.core.inapps.domain.Receipt;
import com.etermax.spacehorse.core.inapps.domain.market.ReceiptFactory;
import com.etermax.spacehorse.core.inapps.error.InAppsErrors;
import com.etermax.spacehorse.core.inapps.error.InAppsException;
import com.etermax.spacehorse.core.user.model.Platform;

public class ReceiptFactoryTest {

	private final String iosReceiptRaw = "{\"Store\":\"AppleAppStore\",\"TransactionID\":\"1000000369222841\","
			+ "\"Payload\":\"\"}";

	@Test
	public void testIosReceiptCreation() {
		try {
			List<Receipt> iosReceipts = ReceiptFactory.createReceipts(Platform.IOS, iosReceiptRaw);
			assertNotNull(iosReceipts);
			assertTrue(iosReceipts.size() == 1);
		} catch(InAppsException ex) {
			if (!isTimeoutError(ex)) {
				Assert.fail(ex.toString());
			}
		}
	}

	private boolean isTimeoutError(InAppsException ex) {
		return ex.getCode() == InAppsErrors.SERVER_TIMEOUT_CODE;
	}
}